INSERT INTO TB_INVTRLM_CODE(INVT_FLD_CD, INVT_FLD_NM, INVT_FLD_CN, ITM_KWRD, USE_BGNG_YMD, USE_END_YMD, CD_ORDR, USE_YN, RGTR_NO, REG_YMD) VALUES ('IRC2301', '수출', '농식품경영체 중 국내산 농·임산물, 가공식품 및 스마트팜(시공·
시설자재·기자재 등) 등을 해외에 수출하(려)는 경영체', '농업, 식품, 제조, 유통', '20230101', '20231231', 1, 'Y', '100000', now());
INSERT INTO TB_INVTRLM_CODE(INVT_FLD_CD, INVT_FLD_NM, INVT_FLD_CN, ITM_KWRD, USE_BGNG_YMD, USE_END_YMD, CD_ORDR, USE_YN, RGTR_NO, REG_YMD) VALUES ('IRC2302', '(20~21)농식품벤처', '사업 개시 후 5년 미만으로 R&D기술, 새로운 비즈니스 모델 등을 활용하여 농업분야 가치창조를 모색하면서 다음 ①, ②, ③, ④ 중 하나의 조건을 충족하는 농식품경영체
   ① 농업계열 고교 및 농식품계열 대학 졸업자가 대표자, 최대주주이거나, 등기임원 중 2인 이상인 경영체
   ② 대표자가 만 39세 이하 이거나, 만 39세 이하 임직원 비중이 50% 이상인 경영체 
   ③ 사업 준비단계 또는 사업개시 후 3년 미만의 농식품경영체
   ④ 스마트팜 보육센터 수료생 창업 경영체', '농업, 식품, 제조, 유통
(한우가공, HRM, 버섯, 차류, 식자재)', '20230101', '20231231', 2, 'Y', '100000', now());
INSERT INTO TB_INVTRLM_CODE(INVT_FLD_CD, INVT_FLD_NM, INVT_FLD_CN, ITM_KWRD, USE_BGNG_YMD, USE_END_YMD, CD_ORDR, USE_YN, RGTR_NO, REG_YMD) VALUES ('IRC2303', '(21~22)스마트농업', '다음 ①, ② 중 하나에 해당하는 농식품경영체
  ① 스마트농업 : ICT 및 각종 첨단 기술과 농업 생산을 비롯해 농업 밸류체인(생산 준비단계, 생산, 유통 등) 전반을 접목한 농업의 스마트화를 선도할 농식품경영체
  ② 탄소중립 : 혁신기술을 활용하여 농업분야의 탄소저감 효과를 가져올 수 있는 농식품경영체', '양돈, 스마트팜, 축산물, 동물용의약품', '20230101', '20231231', 3, 'Y', '100000', now());
INSERT INTO TB_INVTRLM_CODE(INVT_FLD_CD, INVT_FLD_NM, INVT_FLD_CN, ITM_KWRD, USE_BGNG_YMD, USE_END_YMD, CD_ORDR, USE_YN, RGTR_NO, REG_YMD) VALUES ('IRC2304', '(21~22)그린바이오', '* 농식품경영체 중, 그린바이오 5대 분야
* 사업을 영위하(려)는 농식품경영체
