INSERT INTO TB_INVTRLM_CODE(INVT_FLD_CD, INVT_FLD_NM, INVT_FLD_CN, ITM_KWRD, USE_BGNG_YMD, USE_END_YMD, CD_ORDR, USE_YN, RGTR_NO, REG_YMD) VALUES ('IRC2305', '수산일반', '수산업, 수산식품분야 사업을 영위하(려)는 경영체 또는 수산업 관련 업무에 종사하는 자, 원양산업자 및 수산 관련 R&D에 종사하는 자', '어류, 활어, 수산물, 건어물, 어묵, 유통, 건기식', '20230101', '20231231', 5, 'Y', '100000', now());
INSERT INTO TB_INVTRLM_CODE(INVT_FLD_CD, INVT_FLD_NM, INVT_FLD_CN, ITM_KWRD, USE_BGNG_YMD, USE_END_YMD, CD_ORDR, USE_YN, RGTR_NO, REG_YMD) VALUES ('IRC2306', '(20~22)영파머스', ' 농식품경영체 중 1차 농산업을 영위하(려)는 다음 ① ∼ ③ 중 하나에 해당하는 농식품경영체
