INSERT INTO TB_INVTRLM_CODE(INVT_FLD_CD, INVT_FLD_NM, INVT_FLD_CN, ITM_KWRD, USE_BGNG_YMD, USE_END_YMD, CD_ORDR, USE_YN, RGTR_NO, REG_YMD) VALUES ('IRC2313', '경북지역특성화', '농식품경영체로서 ① 출자한 지자체 내 본점이나 주된 사무소의 소재지를 두고 있는(두려는) 경영체 또는 ② 출자 지자체 내 투자를 통해 고용창출 효과가 예상되는 경영체 ', '양계, 데이터, 플랫폼, 스마트팜', '20230101', '20231231', 13, 'Y', '100000', now());
